mapper.select:
SELECT MAX(R.ORDER_DATE) ORDER_DATE
FROM ORDER_INFO R,USER_SHOP S
WHERE R.USER_ID = #{userId}  AND S.USER_ID = #{userId}
    AND S.SHOP_STATE = '1' AND S.SHOP_ID = R.SHOP_ID

    AND R.SHOP_ID = #{shopId}

SELECT O.OI_ID oiId, O.ORDER_ID orderId, O.SHOP_ID shopId, S.SHOP_NAME shopName, DATE_FORMAT(O.ORDER_DATE,'%Y-%m-%d') orderDateStr, O.SUPPLY_ID supplyId, O.GOODS_ID goodsId, O.GOODS_NAME goodsName, O.ORDER_STATE orderState, O.SALE_AMOUNT saleAmount, O.PAYMENT_AMOUNT paymentAmount, O.COST_AMOUNT costAmount, O.PAYOUT_AMOUNT payoutAmount, O.TOTAL_COST totalCost, O.GROSS_PROFIT grossProfit, O.GROSS_PROFIT_RATE grossProfitRate, O.REMARK remark FROM USER_SHOP S,ORDER_INFO O WHERE O.SHOP_ID = S.SHOP_ID AND S.SHOP_STATE='1' AND O.USER_ID = #{obj.userId} AND S.USER_ID = #{obj.userId}
AND O.ORDER_DATE >= STR_TO_DATE(#{obj.startDateStr},'%Y-%m-%d')
AND O.ORDER_DATE <= STR_TO_DATE(#{obj.endDateStr},'%Y-%m-%d')
AND O.ORDER_ID = #{obj.orderId}
AND O.SHOP_ID = #{obj.shopId}
AND O.SUPPLY_ID = #{obj.supplyId}
AND O.GOODS_ID = #{obj.goodsId}
AND O.ORDER_STATE = #{obj.orderState}
ORDER BY O.ORDER_DATE DESC,O.ORDER_ID DESC
